import React, { useEffect } from "react";
import { Row, Col, Container } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../components/Loader";
import Message from "../components/Message";
import Product from "../components/Product";
import BlogPost from "../components/BlogPost";
import PillarCarousel from "../components/PillarCarousel";
import { getTopProducts } from "../actions/productActions";
import { listBlogs } from "../actions/blogActions";

const HomeScreen = () => {
  const dispatch = useDispatch();

  const topProducts = useSelector((state) => state.topProducts);
  const { loading, error, products } = topProducts;

  const blogList = useSelector((state) => state.blogList);
  const { loading: blogLoading, error: blogError, blogs } = blogList;

  useEffect(() => {
    dispatch(listBlogs());
    dispatch(getTopProducts());
  }, [dispatch]);
  return (
    <>
      <h1>Top Products</h1>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <Row className="mb-3">
          {products &&
            products.map((product) => (
              <Col sm={12} md={6} lg={4} xl={3}>
                <Product product={product} />
              </Col>
            ))}
          <Col sm={12} md={6} lg={4} xl={3}>
            <i class="fas fa-chevron-right fa-7x justify-content-md-center"></i>
          </Col>
        </Row>
      )}

      <h1>Community Writing Blog</h1>
      {blogLoading ? (
        <Loader />
      ) : blogError ? (
        <Message variant="danger">{blogError}</Message>
      ) : (
        <Row>
          <Col>
            {blogs &&
              blogs.map((blog) => (
                <Row md={8}>
                  <BlogPost blog={blog} />
                </Row>
              ))}
          </Col>

          <Col md={4}>
            <PillarCarousel className="my-3" />
          </Col>
        </Row>
      )}
    </>
  );
};

export default HomeScreen;
