import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Row, Col, Modal, Button, Form, Image } from "react-bootstrap";
import BlogPost from "../components/BlogPost";
import FormContainer from "../components/FormContainer";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { listBlogs } from "../actions/blogActions";

const BlogScreen = () => {
  const [postShow, setPostShow] = useState(false);
  const [postBody, setPostBody] = useState("");
  const [postTitle, setPostTitle] = useState("");

  const handlePostClose = () => setPostShow(false);
  const handlePostShow = () => setPostShow(true);

  const blogList = useSelector((state) => state.blogList);
  const { loading, error, blogs } = blogList;

  const createBlog = useSelector((state) => state.createBlog);
  const { success } = createBlog;

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(listBlogs());
  }, [dispatch, success]);
  const submitHandler = () => {
    //asas
  };
  return (
    <>
      <Row>
        <Col md={10}>
          <h1>Community Writing Blog</h1>{" "}
        </Col>
        <Col>
          <Button variant="outline-dark like-btn" onClick={handlePostShow}>
            Create a Post <i class="fas fa-4x fa-edit"></i>
          </Button>
        </Col>
      </Row>
      <hr
        style={{
          color: "black",
          backgroundColor: "black",
          height: 5,
        }}
      />
      <Modal show={postShow} onHide={handlePostClose}>
        <Modal.Header closeButton>
          <Modal.Title>Leave a Comment</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <FormContainer>
            <Form onSubmit={submitHandler}>
              <Form.Group controlId="title">
                <Form.Control
                  value={postTitle}
                  onChange={(e) => setPostTitle(e.target.value)}
                  as="text"
                ></Form.Control>
              </Form.Group>
              <Form.Group controlId="body">
                <Form.Control
                  value={postBody}
                  onChange={(e) => setPostBody(e.target.value)}
                  as="textarea"
                ></Form.Control>
              </Form.Group>
              <Button
                variant="dark-btn"
                type="submit"
                onClick={handlePostClose}
              >
                Submit Blog Post
              </Button>
            </Form>
          </FormContainer>
        </Modal.Body>
      </Modal>
      {loading && <Loader />}
      {error && <Message variant="danger">{error}</Message>}
      <Row>
        <Col>
          {blogs &&
            blogs.map((blog) => (
              <Row md={8}>
                <Col md={6}>
                  <BlogPost blog={blog} />
                </Col>
                {blog.img && (
                  <Col md={2}>
                    <Image
                      fluid
                      rounded
                      src={blog.img}
                      alt={blog.title}
                    ></Image>
                  </Col>
                )}
              </Row>
            ))}
        </Col>
      </Row>
    </>
  );
};

export default BlogScreen;
