import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { Table, Row, Button } from "react-bootstrap";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { getCart, updateCart } from "../actions/cartActions";

const CartScreen = () => {
  const dispatch = useDispatch();

  const cartGet = useSelector((state) => state.getCart);
  const { cartItems, loading, error } = cartGet;

  useEffect(() => {
    dispatch(getCart());
  }, [dispatch, cartItems]);

  const cartHandler = () => {
    dispatch(updateCart(item.qty, item._id, item.price));
  };

  return (
    <Row>
      <h1>Shopping Cart</h1>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : cartItems.length === 0 ? (
        <Message>
          You cart is empty <Link to="/">Go Back</Link>
        </Message>
      ) : (
        <Table striped bordered hover responsive className="table-sm">
          <thead>
            <tr>
              <th></th>
              <th></th>
              <th>Price</th>
              <th>Qty</th>
              <th>Total</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((item) => (
              <tr key={item._id}>
                <td>{item.image}</td>
                <td>{item.name}</td>
                <td>{item.price}</td>
                <td>
                  {item.qty}
                  <Button>
                    <i class="fas fa-sort-up"></i>
                  </Button>
                  <Button>
                    <i class="fas fa-sort-down"></i>
                  </Button>
                </td>
                <td>{item.totalPrice}</td>
                <td>
                  <Button>
                    <i class="fas fa-trash-alt"></i>
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </Row>
  );
};

export default CartScreen;
