import React, { useEffect } from "react";
import { Carousel, Col, Image } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { getTopProducts } from "../actions/productActions";
import Loader from "./Loader";
import Message from "./Message";

const TopProductsCarousel = () => {
  const dispatch = useDispatch();

  const topProducts = useSelector((state) => state.topProducts);
  const { loading, error, products } = topProducts;

  useEffect(() => {
    dispatch(getTopProducts());
  }, [dispatch]);
  return loading ? (
    <Loader />
  ) : error ? (
    <Message variant="danger">{error}</Message>
  ) : (
    <Carousel>
      {products.map((product) => (
        <Carousel.Item
          key={product._id}
          className="d-flex align-items-center product-carousel"
        >
          <Col md={4}>
            <Image src={product.image} alt={product.name} fluid />
          </Col>
          <Col md={8} className="d-flex flex-column text-left carousel-body">
            <h3>{product.name}</h3>
            <h2>{product.price}</h2>
            <p>{product.description}</p>
          </Col>
        </Carousel.Item>
      ))}
    </Carousel>
  );
};

export default TopProductsCarousel;
