import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Col, Row, Form, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { updateProfile, getUserDetails } from "../actions/userActions";

const ProfileScreen = () => {
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [authorRequest, setAuthorRequest] = useState(false);
  const [message, setMessage] = useState(null);

  const navigate = useNavigate();
  const dispatch = useDispatch();

  // const orderList = useSelector((state) => state.orderList);
  // const { loading: orderLoading, error: orderError, orders } = orderList;

  // const myBlogs = useSelector((state) => state.myBlogs);
  // const { loading: blogLoading, error: blogError, blogs } = myBlogs;

  useEffect(() => {
    //mp
  }, [dispatch]);

  const toggleAuthor = () => {
    if (!authorRequest) {
      setAuthorRequest(true);
    } else {
      setAuthorRequest(false);
    }
  };

  const submitHandler = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setMessage("Passwords do not match");
    } else {
      dispatch(updateProfile(name, email, password, authorRequest));
    }
  };

  return (
    <>
      <Row>
        <Col md={3}>
          <h1>User Profile</h1>
          {loading && <Loader />}
          {error && <Message variant="danger">{error}</Message>}
          {message && <Message variant="alert">{message}</Message>}
          {success && <Message variant="success">Profile Updated</Message>}
          {userInfo && (
            <Form onSubmit={submitHandler}>
              <Form.Group controlId="name">
                <Form.Label>Name</Form.Label>
                <Form.Control
                  type="name"
                  value={name}
                  placeholder="Enter name"
                  onChange={(e) => setName(e.target.value)}
                ></Form.Control>
              </Form.Group>

              <Form.Group controlId="email">
                <Form.Label>Email Address</Form.Label>
                <Form.Control
                  type="email"
                  value={email}
                  placeholder="Enter email"
                  onChange={(e) => setEmail(e.target.value)}
                ></Form.Control>
              </Form.Group>
              <Form.Group controlId="password">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  value={password}
                  placeholder="Enter password"
                  onChange={(e) => setPassword(e.target.value)}
                ></Form.Control>
              </Form.Group>
              <Form.Group controlId="confirmPassword">
                <Form.Label>Confirm Password</Form.Label>
                <Form.Control
                  type="confirmPassword"
                  value={confirmPassword}
                  placeholder="Enter Password"
                  onChange={(e) => setConfirmPassword(e.target.value)}
                ></Form.Control>
              </Form.Group>
              <Row>
                <Col md={1}>
                  <Button
                    onClick={toggleAuthor()}
                    variant={!authorRequest ? "secondary" : "success"}
                  >
                    Request Author
                  </Button>
                </Col>
                <Col md={1}>
                  <Button type="submit" variant="primary">
                    Update Profile
                  </Button>
                </Col>
              </Row>
            </Form>
          )}
        </Col>
      </Row>
    </>
  );
};

export default ProfileScreen;
