import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { LinkContainer } from "react-router-bootstrap";
import { Col, Row, Form, Button, Card, Table } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { updateProfile, getUserDetails } from "../actions/userActions";
import { listMyOrders } from "../actions/orderActions";
import { UPDATE_PROFILE_RESET } from "../constants/userConstants";

const ProfileScreen = () => {
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [authorRequest, setAuthorRequest] = useState(null);
  const [message, setMessage] = useState(null);
  const [editState, setEditState] = useState(null);

  const navigate = useNavigate();
  const dispatch = useDispatch();

  const userDetails = useSelector((state) => state.userDetails);
  const { loading, error, user } = userDetails;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const userUpdateProfile = useSelector((state) => state.userUpdateProfile);
  const { success } = userUpdateProfile;

  const orderList = useSelector((state) => state.orderList);
  const { orders, loading: orderLoading, error: orderError } = orderList;

  useEffect(() => {
    if (!userInfo) {
      navigate.push("/login");
    }
    if (!user || !user.name || success) {
      setEditState(false);
      dispatch({ type: UPDATE_PROFILE_RESET });
      dispatch(getUserDetails());
    } else {
      setName(user.name);
      setEmail(user.email);
      setAuthorRequest(user.authorRequest);
    }
    if (!orders) {
      dispatch(listMyOrders());
    }
  }, [dispatch, userInfo, user, success, orders]);

  const toggleAuthor = () => {
    if (!authorRequest) {
      setAuthorRequest(true);
    } else {
      setAuthorRequest(false);
    }
  };
  console.log(editState);
  const submitHandler = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setMessage("Passwords do not match");
      setTimeout(() => {
        setMessage(false);
      }, 3000);
    } else {
      console.log(123);
      dispatch(updateProfile(name, email, password, authorRequest));
      setPassword("");
      setConfirmPassword("");
    }
  };

  const toggleEdit = () => {
    if (!editState) {
      setEditState(true);
    }
    if (editState) {
      setEditState(false);
    }
  };

  return (
    <>
      <Row>
        {loading && <Loader />}
        {error && <Message variant="danger">{error}</Message>}
        {message && <Message variant="alert">{message}</Message>}
        {success && (
          <Message variant="alert">Profile Successfully Updated!</Message>
        )}
        {userInfo && (
          <Col md={4}>
            <h2>User Profile</h2>
            <Card className="p-3">
              <Form onSubmit={submitHandler}>
                <Form.Group controlId="name">
                  <Form.Label>
                    <strong>Name</strong>
                  </Form.Label>
                  <Form.Control
                    type="name"
                    value={name}
                    placeholder="Enter name"
                    onChange={(e) => setName(e.target.value)}
                    disabled={!editState ? "disabled" : ""}
                  ></Form.Control>
                </Form.Group>
                <Form.Group controlId="email">
                  <Form.Label>
                    <strong>Email</strong>
                  </Form.Label>
                  <Form.Control
                    type="email"
                    value={email}
                    placeholder="Enter email"
                    onChange={(e) => setEmail(e.target.value)}
                    disabled={!editState ? "disabled" : ""}
                  ></Form.Control>
                </Form.Group>
                <Form.Group controlId="password">
                  <Form.Label>
                    <strong>Password</strong>
                  </Form.Label>
                  <Form.Control
                    type="password"
                    value={password}
                    placeholder="Enter password"
                    onChange={(e) => setPassword(e.target.value)}
                    disabled={!editState ? "disabled" : ""}
                  ></Form.Control>
                </Form.Group>
                <Form.Group controlId="confirmPassword">
                  <Form.Label>
                    <strong>Confirm Password</strong>
                  </Form.Label>
                  <Form.Control
                    type="password"
                    value={confirmPassword}
                    placeholder="Enter password"
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    disabled={!editState ? "disabled" : ""}
                  ></Form.Control>
                </Form.Group>
                <Row>
                  {" "}
                  <Col className="mt-3" md={4}>
                    {user && user.isAuthor ? (
                      <Button onClick={toggleAuthor} variant="success" disabled>
                        Authorized as Author
                      </Button>
                    ) : (
                      <Button
                        onClick={toggleAuthor}
                        variant="success"
                        disabled={!editState || user.isAuthor ? "disabled" : ""}
                      >
                        Request Author Role
                      </Button>
                    )}
                  </Col>
                  <Col className="mt-3" md={4}>
                    {!editState && (
                      <Button onClick={toggleEdit} variant="primary">
                        Update Profile
                      </Button>
                    )}
                    {editState && (
                      <Button
                        onClick={(toggleEdit, submitHandler)}
                        variant="info"
                      >
                        Submit Update
                      </Button>
                    )}
                  </Col>
                </Row>
              </Form>
            </Card>{" "}
          </Col>
        )}

        {orderLoading ? (
          <Loader />
        ) : orderError ? (
          <Message varaint="danger">{orderError}</Message>
        ) : !orders ? (
          <Message variant="info">You have no orders</Message>
        ) : (
          <Col md={6}>
            <h2>My Orders</h2>
            <Card>
              <Table bordered hover responsive className="table-sm">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>DATE</th>
                    <th>TOTAL</th>
                    <th>PAID</th>
                    <th>DELIVERED</th>
                    <th></th>
                  </tr>
                </thead>
                <tbody>
                  {orders &&
                    orders.map((order) => (
                      <tr key={order._id}>
                        <td>{order._id}</td>
                        <td>{order.createdAt}</td>
                        <td>{order.totalPrice}</td>
                        <td>
                          {order.isPaid ? (
                            order.isPaid.substring(0, 10)
                          ) : (
                            <i
                              className="fas fa-times"
                              style={{ color: "red" }}
                            ></i>
                          )}
                        </td>
                        <td>
                          {order.isDelivered ? (
                            order.isDelivered.substring(0, 10)
                          ) : (
                            <i
                              className="fas fa-times"
                              style={{ color: "red" }}
                            ></i>
                          )}
                        </td>
                        <td>
                          <LinkContainer to={`/order/${order._id}`}>
                            <Button className="btn-sm" variant="light">
                              Details
                            </Button>
                          </LinkContainer>
                        </td>
                      </tr>
                    ))}
                </tbody>
              </Table>
            </Card>
          </Col>
        )}
      </Row>
    </>
  );
};

export default ProfileScreen;
