                value={text}
                onChange={(e) => setText(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Button type="submit" variant="primary">
              Send Email
            </Button>
          </Form>
        </Card>
      </Row>
    </>
  );
};

export default ContactScreen;
