import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Card, Row, Col, Button, Modal } from "react-bootstrap";
import Message from "./Message";
import Loader from "./Loader";
import { likeBlog } from "../actions/blogActions";

const BlogPost = ({ blog }) => {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const dispatch = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const likeHandler = () => {
    dispatch(likeBlog(blog._id));
  };

  return (
    <Card className="mb-3" key={blog._id}>
      <Card.Body>
        <Card.Title>{blog.title}</Card.Title>
        <Card.Subtitle className="text-muted mb-2">
          {`${blog.author} - ${blog.createdAt.toString().substring(0, 10)}`}
        </Card.Subtitle>
        <Card.Text>{blog.body}</Card.Text>
        <Row>
          <Col md={2}>
            {blog.likes.find((l) => l.userid === userInfo._id) ? (
              <Button variant="outline-dark like-btn" onClick={likeHandler}>
                {blog.likes.length}{" "}
                <i style={{ color: "red" }} className=" fas fa-heart"></i>
              </Button>
            ) : (
              <Button variant="outline-dark like-btn" onClick={likeHandler}>
                {blog.likes.length}{" "}
                <i style={{ color: "red" }} className="far fa-heart"></i>
              </Button>
            )}
          </Col>
          {blog.replies.length > 0 && (
            <Col md={2}>
              <Button onClick={handleShow} variant="outline-dark like-btn">
                View Comments
              </Button>
              <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                  <Modal.Title>Post Replies</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  {blog.replies.map((r) => (
                    <Card key={r._id}>
                      {" "}
                      <Card.Body>
                        <Card.Title>{r.username}</Card.Title>
                        <Card.Subtitle className="text-muted mb-2">
                          {r.createdAt.toString().substring(0, 10)}
                        </Card.Subtitle>
                        <Card.Text>{r.comment}</Card.Text>
                      </Card.Body>
                    </Card>
                  ))}
                </Modal.Body>
              </Modal>
            </Col>
          )}
        </Row>
      </Card.Body>
    </Card>
  );
};

export default BlogPost;
