import React from "react";
import { Carousel, Image } from "react-bootstrap";

const TopProductsCarousel = ({ product }) => {
  return (
    <Carousel.Item
      key={product._id}
      className="d-flex align-items-center product-carousel"
    >
      <Col md={4}>
        <Image src={product.img} alt={product.name} fluid />
      </Col>
      <Col md={8} className="d-flex flex-column text-left carousel-body">
        <h3>{product.name}</h3>
        <h2>{product.price}</h2>
        <p>{product.description}</p>
      </Col>
    </Carousel.Item>
  );
};

export default TopProductsCarousel;
