import { Card } from "react-bootstrap";
import { blogPosts } from "../blog";

const BlogPost = () => {
  return (
    <Card>
      <Card.Body>
        <Card.Title>{blog.title[0]}</Card.Title>
        <Card.Subtitle className="text-muted mb-2">
          {`${blog.author[0]} - ${blog.date[0]}`}
        </Card.Subtitle>
        <Card.Text>{blog.body[0]}</Card.Text>
      </Card.Body>
    </Card>
  );
};

export default BlogPost;
