import axios from "axios";
import {
  BLOG_LIKE_FAIL,
  BLOG_LIKE_REQUEST,
  BLOG_LIKE_SUCCESS,
  BLOG_LIST_FAIL,
  BLOG_LIST_REQUEST,
  BLOG_LIST_SUCCESS,
  GET_MY_BLOGS_FAIL,
  GET_MY_BLOGS_REQUEST,
  GET_MY_BLOGS_SUCCESS,
  SINGLE_BLOG_FAIL,
  SINGLE_BLOG_REQUEST,
  SINGLE_BLOG_SUCCESS,
} from "../constants/blogConstants";

export const listBlogs = () => async (dispatch) => {
  try {
    dispatch({ type: BLOG_LIST_REQUEST });

    const { data } = await axios.get("api/blogs");

    dispatch({ type: BLOG_LIST_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: BLOG_LIST_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

export const likeBlog = (id) => async (dispatch, getState) => {
  try {
    dispatch({ type: BLOG_LIKE_REQUEST });

    const {
      userLogin: { userInfo },
    } = getState();

    const config = {
      headers: {
        Authorization: `Bearer ${userInfo.token}`,
      },
    };

    const { data } = await axios.post("api/blogs/like", { id }, config);

    dispatch({ type: BLOG_LIKE_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: BLOG_LIKE_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

export const getSingleBlog = (id) => async (dispatch, getState) => {
  try {
    dispatch({ type: SINGLE_BLOG_REQUEST });

    const { data } = await axios.get(`api/blogs/${id}`);

    dispatch({ type: SINGLE_BLOG_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: SINGLE_BLOG_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

export const listMyBlogs = () => async (dispatch, getState) => {
  try {
    dispatch({ type: GET_MY_BLOGS_REQUEST });
    const {
      userLogin: { userInfo },
    } = getState();

    const config = {
      headers: {
        Authorization: `Bearer ${userInfo.token}`,
      },
    };
    const { data } = await axios.get("api/blogs/profile", config);

    dispatch({ type: GET_MY_BLOGS_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: GET_MY_BLOGS_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};
