import {
  BLOG_COMMENT_FAIL,
  BLOG_COMMENT_REQUEST,
  BLOG_COMMENT_SUCCESS,
  BLOG_LIKE_FAIL,
  BLOG_LIKE_REQUEST,
  BLOG_LIKE_SUCCESS,
  BLOG_LIST_FAIL,
  BLOG_LIST_REQUEST,
  BLOG_LIST_SUCCESS,
} from "../constants/blogConstants";

export const blogListReducer = (state = { blogs: [] }, action) => {
  switch (action.type) {
    case BLOG_LIST_REQUEST:
      return { loading: true, blogs: [] };
    case BLOG_LIST_SUCCESS:
      return { loading: false, blogs: action.payload };
    case BLOG_LIST_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export const blogLikeReducer = (state = {}, action) => {
  switch (action.type) {
    case BLOG_LIKE_REQUEST:
      return { loading: true };
    case BLOG_LIKE_SUCCESS:
      return { loading: false, success: true };
    case BLOG_LIKE_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export const blogCommentReducer = (state = {}, action) => {
  switch (action.type) {
    case BLOG_COMMENT_REQUEST:
      return { loading: true };
    case BLOG_COMMENT_SUCCESS:
      return { loading: false, success: true };
    case BLOG_COMMENT_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export const topBlogReducer = (state = { blogs: [] }, action) => {
  switch (action.type) {
    case TOP_BLOG_REQUEST:
      return { loading: true };
    case TOP_BLOG_SUCCESS:
      return { loading: false, blogs: action.payload };
    case TOP_BLOG_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};
