import axios from "axios";
import {
  ADD_CART_FAIL,
  ADD_CART_REQUEST,
  ADD_CART_SUCCESS,
  DOWN_CART_FAIL,
  DOWN_CART_REQUEST,
  DOWN_CART_SUCCESS,
  GET_CART_FAIL,
  GET_CART_REQUEST,
  GET_CART_SUCCESS,
  REMOVE_CART_FAIL,
  REMOVE_CART_REQUEST,
  REMOVE_CART_SUCCESS,
  UP_CART_FAIL,
  UP_CART_REQUEST,
  UP_CART_SUCCESS,
} from "../constants/cartConstants";

export const addItemToCart =
  (name, price, image, _id, qty, options = null, optionName = null) =>
  async (dispatch, getState) => {
    try {
      dispatch({ type: ADD_CART_REQUEST });
      console.log(name, price, image, _id, qty, options);
      const {
        userLogin: { userInfo },
      } = getState();
      console.log(userInfo);
      const config = {
        headers: {
          Authorization: `Bearer ${userInfo.token}`,
        },
      };

      const { data } = await axios.post(
        "/api/cart",
        { name, price, image, _id, qty, options },
        config
      );
      console.log(data);
      dispatch({ type: ADD_CART_SUCCESS, payload: data });
    } catch (error) {
      dispatch({
        type: ADD_CART_FAIL,
        payload:
          error.response && error.response.data.message
            ? error.response.data.message
            : error.message,
      });
    }
  };

export const cartUp = (qty, _id, price) => async (dispatch, getState) => {
  try {
    dispatch({ type: UP_CART_REQUEST });

    const {
      userLogin: { userInfo },
    } = getState();

    const config = {
      headers: {
        Authorization: `Bearer ${userInfo.token}`,
      },
    };
    console.log(qty, _id);
    const { data } = await axios.put(
      "/api/cart/up",
      { qty, _id, price },
      config
    );

    dispatch({ type: UP_CART_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: UP_CART_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

export const cartRemove = (_id) => async (dispatch, getState) => {
  try {
    dispatch({ type: REMOVE_CART_REQUEST });

    const {
      userLogin: { userInfo },
    } = getState();

    const config = {
      headers: {
        Authorization: `Bearer ${userInfo.token}`,
      },
    };

    const { data } = await axios.put("/api/cart/remove", { _id }, config);

    dispatch({ type: REMOVE_CART_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: REMOVE_CART_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

export const cartDown = (qty, _id, price) => async (dispatch, getState) => {
  try {
    dispatch({ type: DOWN_CART_REQUEST });

    const {
      userLogin: { userInfo },
    } = getState();

    const config = {
      headers: {
        Authorization: `Bearer ${userInfo.token}`,
      },
    };

    const { data } = await axios.put(
      "/api/cart/dwn",
      { qty, _id, price },
      config
    );

    dispatch({ type: DOWN_CART_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: DOWN_CART_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

export const getCart = () => async (dispatch, getState) => {
  try {
    dispatch({ type: GET_CART_REQUEST });

    const {
      userLogin: { userInfo },
    } = getState();

    const config = {
      headers: {
        Authorization: `Bearer ${userInfo.token}`,
      },
    };

    const { data } = await axios.get("/api/cart", config);

    dispatch({ type: GET_CART_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: GET_CART_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};
