import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { Col, Row, Button, Image, ListGroup } from "react-bootstrap";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { getCart, controlCart } from "../actions/cartActions";

const CartScreen = () => {
  const dispatch = useDispatch();

  const cartGet = useSelector((state) => state.cartGet);
  const { cartItems, loading, error } = cartGet;

  const cartControl = useSelector((state) => state.cartControl);
  const {
    cartItems: controlItems,
    loading: controlLoading,
    error: controlError,
  } = cartControl;

  useEffect(() => {
    dispatch(getCart());
  }, [dispatch, controlItems]);

  return (
    <Row>
      <Col md={10}>
        <h1>Shopping Cart</h1>
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : cartItems.length === 0 ? (
          <Message>
            You cart is empty <Link to="/">Go Back</Link>
          </Message>
        ) : (
          <ListGroup variant="flush">
            {cartItems.map((item) => (
              <ListGroup.Item key={item._id}>
                <Row>
                  <Col md={2}>
                    <Image
                      src={item.image}
                      alt={item.name}
                      fluid
                      rounded
                      sm={1}
                    />
                  </Col>
                  <Col md={2}>
                    <Link to={`/product/${item.productId}`}>{item.name}</Link>
                  </Col>
                  <Col md={2}>{item.price}</Col>
                  <Col md={2}>
                    <Row>
                      <Col md={1}>{item.qty}</Col>
                    </Row>
                    <Row>
                      <Col md={1}>
                        {" "}
                        <Button
                          onClick={() => dispatch(controlCart(item._id, "up"))}
                        >
                          <i class="fas fa-sort-up"></i>
                        </Button>{" "}
                        <Button
                          onClick={() =>
                            dispatch(controlCart(item._id, "down"))
                          }
                        >
                          <i class="fas fa-sort-down"></i>
                        </Button>
                      </Col>
                    </Row>
                  </Col>
                  <Col md={2}>{item.totalPrice}</Col>
                  <Col md={2}>
                    {" "}
                    <Button
                      onClick={() => dispatch(controlCart(item._id, "trash"))}
                    >
                      <i class="fas fa-trash-alt"></i>
                    </Button>
                  </Col>
                </Row>
              </ListGroup.Item>
            ))}
          </ListGroup>
        )}
      </Col>
    </Row>
  );
};

export default CartScreen;
