import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Card, Row, Col, Button } from "react-bootstrap";
import { likeBlog, listBlogs } from "../actions/blogActions";
import { BLOG_LIKE_RESET } from "../constants/blogConstants";

const BlogPost = ({ blog }) => {
  const dispatch = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const blogLike = useSelector((state) => state.blogLike);
  const { success, blogLikes } = blogLike;

  useEffect(() => {
    if (success) {
      dispatch(listBlogs());
    }
  }, [dispatch, success]);

  const likeHandler = () => {
    dispatch(likeBlog(blog._id));
  };

  return (
    <Card className="mb-3" key={blog._id}>
      <Card.Body>
        <Card.Title>{blog.title}</Card.Title>
        <Card.Subtitle className="text-muted mb-2">
          {`${blog.author} - ${blog.createdAt.toString().substring(0, 10)}`}
        </Card.Subtitle>
        <Card.Text>{blog.body}</Card.Text>
        <Row>
          <Col md={2}>
            {blog.likes.find((l) => l.userid === userInfo._id) ? (
              <Button variant="outline-dark" onClick={likeHandler}>
                {blog.likes.length}{" "}
                <i style={{ color: "red" }} className=" fas fa-heart"></i>
              </Button>
            ) : (
              <Button variant="outline-dark" onClick={likeHandler}>
                {blog.likes.length}{" "}
                <i style={{ color: "red" }} className="far fa-heart"></i>
              </Button>
            )}
          </Col>
          <Col md={1}>
            {blog.replies.map((r) => (
              <Row>{r.comment}</Row>
            ))}
          </Col>
        </Row>
      </Card.Body>
    </Card>
  );
};

export default BlogPost;
