import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { Table, Row } from "react-bootstrap";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { getCart, updateCart } from "../actions/cartActions";

const CartScreen = () => {
  const dispatch = useDispatch();

  const cartGet = useSelector((state) => state.getCart);
  const { cartItems, loading, error } = cartGet;

  useEffect(() => {
    dispatch(getCart());
  }, [dispatch, cartItems]);

  const cartHandler = () => {
    dispatch(updateCart(qty, _id, price));
  };

  return (
    <>
      <h1>Shopping Cart</h1>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : cartItems.length === 0 ? (
        <Message>
          You cart is empty <Link to="/">Go Back</Link>
        </Message>
      ) : (
        <Row>
          <Table></Table>
        </Row>
      )}
    </>
  );
};

export default CartScreen;
