import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Col, Row, Form, Button, Card } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { updateProfile, getUserDetails } from "../actions/userActions";
import { listMyOrders } from "../actions/orderActions";
import { UPDATE_PROFILE_RESET } from "../constants/userConstants";

const ProfileScreen = () => {
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [authorRequest, setAuthorRequest] = useState(null);
  const [message, setMessage] = useState(null);
  const [editState, setEditState] = useState(null);

  const navigate = useNavigate();
  const dispatch = useDispatch();

  const userDetails = useSelector((state) => state.userDetails);
  const { loading, error, user } = userDetails;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const userUpdateProfile = useSelector((state) => state.userUpdateProfile);
  const { success } = userUpdateProfile;

  const orderList = useSelector((state) => state.orderList);
  const { orders, loading: orderLoading, error: orderError } = orderList;

  useEffect(() => {
    if (!userInfo) {
      navigate.push("/login");
    }
    if (!user || !user.name || success) {
      setEditState(false);
      dispatch({ type: UPDATE_PROFILE_RESET });
      dispatch(getUserDetails());
    } else {
      setName(user.name);
      setEmail(user.email);
      setAuthorRequest(user.authorRequest);
    }
    if (!orders) {
      dispatch(listMyOrders());
    }
  }, [dispatch, userInfo, user, success]);

  const toggleAuthor = () => {
    if (!authorRequest) {
      setAuthorRequest(true);
    } else {
      setAuthorRequest(false);
    }
  };
  console.log(editState);
  const submitHandler = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setMessage("Passwords do not match");
      setTimeout(() => {
        setMessage(false);
      }, 3000);
    } else {
      console.log(123);
      dispatch(updateProfile(name, email, password, authorRequest));
      setPassword("");
      setConfirmPassword("");
    }
  };

  const toggleEdit = () => {
    if (!editState) {
      setEditState(true);
    }
    if (editState) {
      setEditState(false);
    }
  };

  return (
    <>
      <Row>
        <h1>User Profile</h1>
        {loading && <Loader />}
        {error && <Message variant="danger">{error}</Message>}
        {message && <Message variant="alert">{message}</Message>}
        {success && (
          <Message variant="alert">Profile Successfully Updated!</Message>
        )}
        {/* {success && <Message variant="success">Profile Updated</Message>} */}
        {userInfo && (
          <Col md={4}>
            <Card className="p-3">
              <Form onSubmit={submitHandler}>
                <Form.Group controlId="name">
                  <Form.Label>
                    <strong>Name</strong>
                  </Form.Label>
                  <Form.Control
                    type="name"
                    value={name}
                    placeholder="Enter name"
                    onChange={(e) => setName(e.target.value)}
                    disabled={!editState ? "disabled" : ""}
                  ></Form.Control>
                </Form.Group>
                <Form.Group controlId="email">
                  <Form.Label>
                    <strong>Email</strong>
                  </Form.Label>
                  <Form.Control
                    type="email"
                    value={email}
                    placeholder="Enter email"
                    onChange={(e) => setEmail(e.target.value)}
                    disabled={!editState ? "disabled" : ""}
                  ></Form.Control>
                </Form.Group>
                <Form.Group controlId="password">
                  <Form.Label>
                    <strong>Password</strong>
                  </Form.Label>
                  <Form.Control
                    type="password"
                    value={password}
                    placeholder="Enter password"
                    onChange={(e) => setPassword(e.target.value)}
                    disabled={!editState ? "disabled" : ""}
                  ></Form.Control>
                </Form.Group>
                <Form.Group controlId="confirmPassword">
                  <Form.Label>
                    <strong>Confirm Password</strong>
                  </Form.Label>
                  <Form.Control
                    type="password"
                    value={confirmPassword}
                    placeholder="Enter password"
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    disabled={!editState ? "disabled" : ""}
                  ></Form.Control>
                </Form.Group>
                <Row>
                  {" "}
                  <Col className="mt-3" md={4}>
                    {user.isAuthor && (
                      <Button onClick={toggleAuthor} variant="success" disabled>
                        Authorized as Author
                      </Button>
                    )}
                    {!user.isAuthor && (
                      <Button
                        onClick={toggleAuthor}
                        variant="success"
                        disabled={!editState || user.isAuthor ? "disabled" : ""}
                      >
                        Request Author Role
                      </Button>
                    )}
                  </Col>
                  <Col className="mt-3" md={4}>
                    {!editState && (
                      <Button onClick={toggleEdit} variant="primary">
                        Update Profile
                      </Button>
                    )}
                    {editState && (
                      <Button
                        onClick={(toggleEdit, submitHandler)}
                        variant="info"
                      >
                        Submit Update
                      </Button>
                    )}
                  </Col>
                </Row>
              </Form>
            </Card>{" "}
          </Col>
        )}
        {orders && (
          <Col md={6}>
            <Card>
              <Table bordered hover responsive className="table-sm">
                <thead>
                  <tr>
                    <th>ID</th>
                  </tr>
                </thead>
              </Table>
            </Card>
          </Col>
        )}
      </Row>
    </>
  );
};

export default ProfileScreen;
