import {
  UPDATE_CART_FAIL,
  UPDATE_CART_REQUEST,
  UPDATE_CART_SUCCESS,
  ADD_ITEM_CART_FAIL,
  ADD_ITEM_CART_REQUEST,
  ADD_ITEM_CART_SUCCESS,
  ADD_ITEM_CART_RESET,
  GET_CART_FAIL,
  GET_CART_REQUEST,
  GET_CART_SUCCESS,
} from "../constants/cartConstants";

export const cartAddReducer = (state = {}, action) => {
  switch (action.type) {
    case ADD_ITEM_CART_REQUEST:
      return { loading: true, success: null };
    case ADD_ITEM_CART_SUCCESS:
      return { loading: false, success: true };
    case ADD_ITEM_CART_FAIL:
      return { loading: false, success: false, error: action.payload };
    case ADD_ITEM_CART_RESET:
      return {};
    default:
      return state;
  }
};

export const cartGetReducer = (state = { cartItems: [] }, action) => {
  switch (action.type) {
    case GET_CART_REQUEST:
      return { loading: true };
    case GET_CART_SUCCESS:
      return { loading: false, cartItems: action.payload };
    case GET_CART_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};
export const cartUpdateReducer = (state = { cartItems: [] }, action) => {
  switch (action.type) {
    case UPDATE_CART_REQUEST:
      return { loading: true };
    case UPDATE_CART_SUCCESS:
      return { loading: false, cartItems: action.payload };
    case UPDATE_CART_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};
