import {
  CREATE_ORDER_FAIL,
  CREATE_ORDER_REQUEST,
  CREATE_ORDER_SUCCESS,
  GET_MY_ORDERS_FAIL,
  GET_MY_ORDERS_REQUEST,
  GET_MY_ORDERS_SUCCESS,
  ORDER_SHIPPING_ADDRESS_FAIL,
  ORDER_SHIPPING_ADDRESS_REQUEST,
  ORDER_SHIPPING_ADDRESS_SUCCESS,
  PAY_ORDER_FAIL,
  PAY_ORDER_REQUEST,
  PAY_ORDER_SUCCESS,
} from "../constants/orderConstants";

export const orderListReducer = (state = { orders: [] }, action) => {
  switch (action.type) {
    case GET_MY_ORDERS_REQUEST:
      return { loading: true };
    case GET_MY_ORDERS_SUCCESS:
      return { loading: false, orders: action.payload };
    case GET_MY_ORDERS_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export const orderCreateReducer = (state = { order: {} }, action) => {
  switch (action.type) {
    case CREATE_ORDER_REQUEST:
      return { loading: true };
    case CREATE_ORDER_SUCCESS:
      return { loading: false, order: action.payload };
    case CREATE_ORDER_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export const orderPayReducer = (state = { paymentMethod: {} }, action) => {
  switch (action.type) {
    case PAY_ORDER_REQUEST:
      return { loading: true };
    case PAY_ORDER_SUCCESS:
      return { loading: false, paymentMethod: action.payload };
    case PAY_ORDER_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export const orderShippingReducer = (
  state = { shippingAddress: {} },
  action
) => {
  switch (action.type) {
    case ORDER_SHIPPING_ADDRESS_REQUEST:
      return { loading: true };
    case ORDER_SHIPPING_ADDRESS_SUCCESS:
      return { loading: false, shippingAddress: action.payload };
    case ORDER_SHIPPING_ADDRESS_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};
