import React from "react";
import { Col, Row } from "react-bootstrap";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";

const PortalScreen = () => {
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  return (
    <Row className="portalRow">
      <Col className="d-flex flex-column sidebar bg-primary" md={1}>
        <div>
          <Link to="/">
            {" "}
            <i class="fa-solid fa-house"></i>
          </Link>
        </div>
        <div>
          <Link to={userInfo ? "/profile" : "/login"}>
            <i class="fa-solid fa-user"></i>{" "}
          </Link>
        </div>
        <div>
          <Link to="/shop">
            <i class="fa-solid fa-bag-shopping"></i>{" "}
          </Link>
        </div>
        <div>
          <Link to="/blog">
            <i class="fa-solid fa-book"></i>{" "}
          </Link>
        </div>
        <div>
          <Link to="/about">
            <i class="fa-solid fa-chalkboard-user"></i>{" "}
          </Link>
        </div>
        <div>
          <Link to="/contact">
            <i class="fa-solid fa-envelope"></i>{" "}
          </Link>
        </div>
      </Col>
      <Col
        className=" portal-body d-flex justify-content-center flex-column align-items-center"
        md={10}
      >
        <div className="portal-icon-body">
          <i class="portal-icon fa-solid fa-mug-hot"></i>
        </div>
        <div>
          <h1>eCo</h1>
        </div>
        <div>
          <h3>Coffee Shop Blog Spot</h3>
        </div>
      </Col>
    </Row>
  );
};

export default PortalScreen;
