import React from "react";
import { Card, Col, Image, ListGroupItem, Row } from "react-bootstrap";

const BlogScreenBlogPosts = ({ blog }) => {
  return (
    <Card className="blogscreen-card" className="m-3" key={blog._id}>
      <div className="blogcard-top">
        {" "}
        <Image className="blogscreen-image" src={blog.image} />
        <h3>{blog.title}</h3>
      </div>
      <ListGroupItem>
        <Row>
          <Col
            className="border-right d-flex flex-column justify-content-center align-items-center"
            md={8}
          >
            <p>{blog.author}</p>
            <p>{blog.createdAt.substring(0, 10)}</p>
          </Col>{" "}
          <Col md={4}>
            <p>
              {blog.likes.length && blog.likes.length}{" "}
              <i style={{ color: "red" }} className=" fas fa-heart"></i>
            </p>
            <p>
              {blog.replies.length && blog.replies.length}{" "}
              <i
                style={{ color: "rgb(195, 216, 226)" }}
                class="fa-solid fa-comment"
              ></i>
            </p>
          </Col>
        </Row>
      </ListGroupItem>
    </Card>
  );
};

export default BlogScreenBlogPosts;
