import {
  ADD_ITEM_CART_FAIL,
  ADD_ITEM_CART_REQUEST,
  ADD_ITEM_CART_SUCCESS,
  ADD_ITEM_CART_RESET,
  GET_CART_FAIL,
  GET_CART_REQUEST,
  GET_CART_SUCCESS,
  REMOVE_ITEM_CART_REQUEST,
  REMOVE_ITEM_CART_SUCCESS,
  REMOVE_ITEM_CART_FAIL,
  UP_CART_REQUEST,
  UP_CART_SUCCESS,
  UP_CART_FAIL,
  DOWN_CART_REQUEST,
  DOWN_CART_SUCCESS,
  DOWN_CART_FAIL,
} from "../constants/cartConstants";

export const cartAddReducer = (state = {}, action) => {
  switch (action.type) {
    case ADD_ITEM_CART_REQUEST:
      return { loading: true, success: null };
    case ADD_ITEM_CART_SUCCESS:
      return { loading: false, success: true };
    case ADD_ITEM_CART_FAIL:
      return { loading: false, success: false, error: action.payload };
    case ADD_ITEM_CART_RESET:
      return {};
    default:
      return state;
  }
};

export const cartGetReducer = (state = { cartItems: [] }, action) => {
  switch (action.type) {
    case GET_CART_REQUEST:
      return { loading: true };
    case GET_CART_SUCCESS:
      return { loading: false, cartItems: action.payload };
    case GET_CART_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};
export const cartRemoveItemReducer = (state = { cartItems: [] }, action) => {
  switch (action.type) {
    case REMOVE_ITEM_CART_REQUEST:
      return { loading: true };
    case REMOVE_ITEM_CART_SUCCESS:
      return { loading: false, cartItems: action.payload };
    case REMOVE_ITEM_CART_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};
export const cartQtyUpReducer = (state = { cartItems: [] }, action) => {
  switch (action.type) {
    case UP_CART_REQUEST:
      return { loading: true };
    case UP_CART_SUCCESS:
      return { loading: false, cartItems: action.payload };
    case UP_CART_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};
export const cartQtyDownReducer = (state = { cartItems: [] }, action) => {
  switch (action.type) {
    case DOWN_CART_REQUEST:
      return { loading: true };
    case DOWN_CART_SUCCESS:
      return { loading: false, cartItems: action.payload };
    case DOWN_CART_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};
