import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Card, Row, Col, Button, Modal, Form } from "react-bootstrap";
import Message from "./Message";
import Loader from "./Loader";
import { commentBlog, likeBlog } from "../actions/blogActions";
import FormContainer from "./FormContainer";

const BlogPost = ({ blog }) => {
  const [message, setMessage] = useState("");
  const [show, setShow] = useState(false);
  const [secondShow, setSecondShow] = useState(false);
  const [comment, setComment] = useState("");

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleSecondClose = () => setSecondShow(false);
  const handleSecondShow = () => setSecondShow(true);

  const dispatch = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const likeHandler = () => {
    if (!userInfo) {
      setMessage(`Must be logged in to leave a like`);
      setTimeout(() => {
        setMessage(false);
      }, 5000);
    } else {
      dispatch(likeBlog(blog._id));
    }
  };

  const submitHandler = (e) => {
    e.preventDefault();
    if (!userInfo) {
      setMessage(`Must be logged in to leave a comment`);
      setTimeout(() => {
        setMessage(false);
      }, 5000);
    } else {
      dispatch(commentBlog(comment, blog._id));
    }
  };

  return (
    <Card className="mb-3" key={blog._id}>
      {message && <Message variant="info">{message}</Message>}
      <Card.Body>
        <Card.Title>{blog.title}</Card.Title>
        <Card.Subtitle className="text-muted mb-2">
          {`${blog.author} - ${blog.createdAt.toString().substring(0, 10)}`}
        </Card.Subtitle>
        <Card.Text>{blog.body}</Card.Text>
        <Row>
          <Col md={2}>
            {userInfo && blog.likes.find((l) => l.userid === userInfo._id) ? (
              <Button variant="outline-dark like-btn" onClick={likeHandler}>
                {blog.likes.length}{" "}
                <i style={{ color: "red" }} className=" fas fa-heart"></i>
              </Button>
            ) : (
              <Button variant="outline-dark like-btn" onClick={likeHandler}>
                {blog.likes.length}{" "}
                <i style={{ color: "red" }} className="far fa-heart"></i>
              </Button>
            )}
          </Col>
          <Col md={3}>
            <Button onClick={handleSecondShow} variant="outline-dark like-btn">
              Leave a Comment
            </Button>
            <Modal show={secondShow} onHide={handleSecondClose}>
              <Modal.Header closeButton>
                <Modal.Title>Leave a Comment</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <FormContainer>
                  <Form onSubmit={submitHandler}>
                    <Form.Group controlId="comment">
                      <Form.Control
                        onChange={(e) => setComment(e.target.value)}
                        as="textarea"
                      ></Form.Control>
                    </Form.Group>
                    <Button
                      variant="dark-btn"
                      type="submit"
                      onClick={handleSecondClose}
                    >
                      Reply
                    </Button>
                  </Form>
                </FormContainer>
              </Modal.Body>
            </Modal>
          </Col>
          {blog.replies.length > 0 && (
            <Col md={3}>
              <Button onClick={handleShow} variant="outline-dark like-btn">
                View Comments
              </Button>
              <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                  <Modal.Title>Post Replies</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  {blog.replies.map((r) => (
                    <Card className="mb-1" key={r._id}>
                      {" "}
                      <Card.Body>
                        <Card.Title>
                          <Col md={8}>{r.username}</Col>
                          <Col md={8}>
                            {" "}
                            <i class="text-right fas fa-times"></i>
                          </Col>
                        </Card.Title>
                        <Card.Subtitle className="text-muted mb-2">
                          {r.createdAt.toString().substring(0, 10)}
                        </Card.Subtitle>
                        <Card.Text>{r.comment}</Card.Text>
                      </Card.Body>
                    </Card>
                  ))}
                </Modal.Body>
              </Modal>
            </Col>
          )}
        </Row>
      </Card.Body>
    </Card>
  );
};

export default BlogPost;
