import React, { useEffect } from "react";
import { Image, Col, Listgroup, Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import Message from "../components/Message";
import Loader from "../components/Loader";
import { getSingleBlog } from "../actions/blogActions";
import Container from "../components/Container";

const SingleBlogScreen = () => {
  const { id } = useParams();
  const dispatch = useDispatch();

  const singleBlog = useSelector((state) => state.singleBlog);
  const { blog, loading, error } = singleBlog;

  useEffect(() => {
    dispatch(getSingleBlog(id));
  }, [id]);

  return (
    <Container>
      {error ? (
        <Message variant="danger">{error}</Message>
      ) : blog ? (
        <Col>
          <Image
            className="soloblog-img img-fluid rounded mx-auto d-block"
            src={blog.image}
          />
          <h1 className="mt-3">{blog.title}</h1>
          <p className="text-muted">
            {blog.author} - {blog.createdAt.substring(0, 10)}
          </p>

          <p>{blog.body}</p>
          <hr />
          <h3>Comments</h3>
          {blog.replies.map((r) => {
            <ListGroup.Item key={r._id}>
              <Row className="justify-space-between">
                <strong>{r.username}</strong>
              </Row>
              <p>{r.createdAt.substring(0, 10)}</p>
              <p>{r.comment}</p>
            </ListGroup.Item>;
          })}
        </Col>
      ) : (
        <Loader />
      )}
    </Container>
  );
};

export default SingleBlogScreen;
