import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  Card,
  Row,
  Col,
  Button,
  Modal,
  Form,
  Toast,
  Image,
  ListGroupItem,
} from "react-bootstrap";
import { Link } from "react-router-dom";
import { likeBlog, commentBlog } from "../actions/blogActions";

const BlogScreenBlogPosts = ({ blog }) => {
  const [message, setMessage] = useState("");
  const [toastShow, setToastShow] = useState(false);
  const [comment, setComment] = useState("");

  const dispatch = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const likeHandler = () => {
    if (!userInfo) {
      setToastShow(true);
      setMessage(`Must be logged in to leave a like`);
      setTimeout(() => {
        setMessage(false);
      }, 5000);
    } else {
      dispatch(likeBlog(blog._id));
    }
  };

  const commentHandler = (e) => {
    e.preventDefault();
    if (!userInfo) {
      setToastShow(true);
      setMessage(`Must be logged in to leave a comment`);
      setTimeout(() => {
        setMessage(false);
      }, 5000);
    } else {
      dispatch(commentBlog(comment, blog._id));
    }
  };
  return (
    <Card rounded className="blogscreen-card" className="m-3" key={blog._id}>
      <Link to={`/blog/${blog._id}`} className="blogcard-top">
        {" "}
        <Card.Img className="blogcard-image" variant="top" src={blog.image} />
        <div className="blogcard-intro">
          <h4>{blog.title}</h4>
          <h5>{blog.author}</h5>
        </div>
      </Link>
      <ListGroupItem>
        <Link to={`/blog/${blog._id}`}>
          {blog.body.length > 150 ? (
            <p>{`${blog.body.substring(0, 150)} ...Read More`}</p>
          ) : (
            <p>{blog.body}</p>
          )}
        </Link>
      </ListGroupItem>
      <Card.Footer>
        <small className="text-muted justify-content-between d-flex">
          <Col md={10}>{blog.createdAt.substring(0, 10)}</Col>
          <Col md={2}>
            {userInfo && blog.likes.find((l) => l.userid === userInfo._id) ? (
              <Button
                variant="outline-dark"
                className="blog-action-btn"
                onClick={likeHandler}
              >
                {blog.likes.length}{" "}
                <i style={{ color: "red" }} className=" fas fa-heart"></i>
              </Button>
            ) : (
              <Button
                variant="outline-dark"
                className="blog-action-btn"
                onClick={likeHandler}
              >
                {blog.likes.length}{" "}
                <i style={{ color: "red" }} className="far fa-heart"></i>
              </Button>
            )}
            {"   "}
            {blog.replies.length && blog.replies.length}{" "}
            <Button className="blog-action-btn">
              <i
                style={{ color: "rgb(195, 216, 226)" }}
                class="fa-solid fa-comment"
              ></i>
            </Button>
          </Col>
        </small>
      </Card.Footer>
    </Card>
  );
};

export default BlogScreenBlogPosts;
