import axios from "axios";
import {
  ADD_ITEM_CART_FAIL,
  ADD_ITEM_CART_REQUEST,
  ADD_ITEM_CART_SUCCESS,
  GET_CART_FAIL,
  GET_CART_REQUEST,
  GET_CART_SUCCESS,
  UPDATE_CART_FAIL,
  UPDATE_CART_REQUEST,
  UPDATE_CART_SUCCESS,
} from "../constants/cartConstants";

export const addItemToCart =
  (name, price, image, _id, qty, options, optionName = null) =>
  async (dispatch, getState) => {
    try {
      dispatch({ type: ADD_ITEM_CART_REQUEST });
      console.log(name, price, image, _id, qty, options);
      const {
        userLogin: { userInfo },
      } = getState();
      console.log(userInfo);
      const config = {
        headers: {
          Authorization: `Bearer ${userInfo.token}`,
        },
      };

      const { data } = await axios.post(
        "/api/cart",
        { name, price, image, _id, qty, options },
        config
      );
      console.log(data);
      dispatch({ type: ADD_ITEM_CART_SUCCESS, payload: data });
    } catch (error) {
      dispatch({
        type: ADD_ITEM_CART_FAIL,
        payload:
          error.response && error.response.data.message
            ? error.response.data.message
            : error.message,
      });
    }
  };

export const updateCart = (cartItem) => async (dispatch, getState) => {
  try {
    dispatch({ type: UPDATE_CART_REQUEST });

    const {
      userLogin: { userInfo },
    } = getState();

    const config = {
      headers: {
        Authorization: `Bearer ${userInfo.token}`,
      },
    };

    const { data } = await axios.put("/api/cart", cartItem, config);

    dispatch({ type: UPDATE_CART_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: UPDATE_CART_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

export const getCart = () => async (dispatch, getState) => {
  try {
    dispatch({ type: GET_CART_REQUEST });

    const {
      userLogin: { userInfo },
    } = getState();

    const config = {
      headers: {
        Authorization: `Bearer ${userInfo.token}`,
      },
    };

    const { data } = await axios.get("/api/cart", {}, config);

    dispatch({ type: GET_CART_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: GET_CART_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};
